// src/components/Navbar.js
import React, { useEffect, useMemo, useRef, useState } from "react";
import { Link, NavLink, useLocation } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

const IconKebab = (props) => (
  <svg viewBox="0 0 24 24" width="18" height="18" aria-hidden="true" {...props}>
    <circle cx="12" cy="5" r="2" />
    <circle cx="12" cy="12" r="2" />
    <circle cx="12" cy="19" r="2" />
  </svg>
);

const IconBell = (props) => (
  <svg viewBox="0 0 24 24" width="18" height="18" aria-hidden="true" {...props}>
    <path
      fill="currentColor"
      d="M12 22a2.5 2.5 0 0 0 2.45-2h-4.9A2.5 2.5 0 0 0 12 22Zm7-6V11a7 7 0 1 0-14 0v5L3 18v1h18v-1l-2-2Z"
    />
  </svg>
);

const IconChat = (props) => (
  <svg viewBox="0 0 24 24" width="18" height="18" aria-hidden="true" {...props}>
    <path
      fill="currentColor"
      d="M4 4h16v12H7l-3 3V4Zm4 7h8v2H8v-2Zm0-4h12v2H8V7Z"
    />
  </svg>
);

const IconStack = (props) => (
  <svg viewBox="0 0 24 24" width="18" height="18" aria-hidden="true" {...props}>
    <path
      fill="currentColor"
      d="M12 2 1 7l11 5 11-5-11-5Zm0 8L1 5v4l11 5 11-5V5l-11 5Zm0 6L1 11v4l11 5 11-5v-4l-11 5Z"
    />
  </svg>
);

const IconChart = (props) => (
  <svg viewBox="0 0 24 24" width="18" height="18" aria-hidden="true" {...props}>
    <path
      fill="currentColor"
      d="M4 19h16v2H4v-2Zm2-8h3v6H6v-6Zm5-5h3v11h-3V6Zm5 3h3v8h-3V9Z"
    />
  </svg>
);

const IconShield = (props) => (
  <svg viewBox="0 0 24 24" width="18" height="18" aria-hidden="true" {...props}>
    <path
      fill="currentColor"
      d="M12 2 4 5v6c0 5 3.4 9.4 8 11 4.6-1.6 8-6 8-11V5l-8-3Zm-1 14-3-3 1.4-1.4L11 13.2l3.6-3.6L16 11l-5 5Z"
    />
  </svg>
);

const IconSettings = (props) => (
  <svg viewBox="0 0 24 24" width="18" height="18" aria-hidden="true" {...props}>
    <path
      fill="currentColor"
      d="M19.14 12.94c.04-.3.06-.62.06-.94s-.02-.64-.06-.94l2.03-1.58a.5.5 0 0 0 .12-.64l-1.92-3.32a.5.5 0 0 0-.6-.22l-2.39.96a7.1 7.1 0 0 0-1.63-.94l-.36-2.54A.5.5 0 0 0 13.9 1h-3.8a.5.5 0 0 0-.49.41l-.36 2.54c-.58.22-1.12.53-1.63.94l-2.39-.96a.5.5 0 0 0-.6.22L2.71 7.47a.5.5 0 0 0 .12.64l2.03 1.58c-.04.3-.06.62-.06.94s.02.64.06.94l-2.03 1.58a.5.5 0 0 0-.12.64l1.92 3.32c.13.22.39.3.6.22l2.39-.96c.5.41 1.05.72 1.63.94l.36 2.54c.04.24.25.41.49.41h3.8c.24 0 .45-.17.49-.41l.36-2.54c.58-.22 1.12-.53 1.63-.94l2.39.96c.21.08.47 0 .6-.22l1.92-3.32a.5.5 0 0 0-.12-.64l-2.03-1.58ZM12 15.5A3.5 3.5 0 1 1 12 8a3.5 3.5 0 0 1 0 7.5Z"
    />
  </svg>
);

const Navbar = () => {
  const { user, logout } = useAuth();
  const isAdmin = user?.role === "ADMIN";
  const location = useLocation();

  const [openMore, setOpenMore] = useState(false);
  const moreRef = useRef(null);

  useEffect(() => setOpenMore(false), [location.pathname]);

  useEffect(() => {
    const onDocClick = (e) => {
      if (!moreRef.current) return;
      if (!moreRef.current.contains(e.target)) setOpenMore(false);
    };
    const onEsc = (e) => e.key === "Escape" && setOpenMore(false);
    document.addEventListener("mousedown", onDocClick);
    document.addEventListener("keydown", onEsc);
    return () => {
      document.removeEventListener("mousedown", onDocClick);
      document.removeEventListener("keydown", onEsc);
    };
  }, []);

  const mainLinks = useMemo(
    () => [
      { to: "/", label: "Home" },
      { to: "/crops", label: "Crops" },
      { to: "/education", label: "Education" },
      { to: "/complaints", label: "Complaints" },
      { to: "/search", label: "Search" },
    ],
    []
  );

  const adminMoreLinks = useMemo(
    () => [
      { to: "/notifications", label: "Notifications", icon: IconBell },
      { to: "/chatbot", label: "Chatbot", icon: IconChat },
      { to: "/subsidies", label: "Subsidies", icon: IconStack },
      { to: "/admin", label: "Admin Panel", icon: IconSettings },
      { to: "/analytics", label: "Analytics", icon: IconChart },
      { to: "/verification", label: "Verification", icon: IconShield },
      { to: "/ledger", label: "Ledger", icon: IconStack },
    ],
    []
  );

  return (
    <div className="navbar">
      <div className="navbar-inner navbar-inner--organized">
        {/* Left side */}
        <div className="nav-left">
          <div className="brand">
            <div className="brand-badge" />
            <Link to="/" style={{ fontWeight: 800 }}>
              AgroTrust
            </Link>
          </div>

          {/* Main links (shown for any logged-in user) */}
          {user && (
            <nav className="nav-links nav-links--tight" aria-label="Primary">
              {mainLinks.map((l) => (
                <NavLink
                  key={l.to}
                  to={l.to}
                  className={({ isActive }) =>
                    `nav-link ${isActive ? "active" : ""}`
                  }
                  end={l.to === "/"}
                >
                  {l.label}
                </NavLink>
              ))}
            </nav>
          )}
        </div>

        {/* Right side */}
        <div className="nav-right nav-right--tight">
          {/* Admin-only kebab menu */}
          {user && isAdmin && (
            <div className="more-wrap" ref={moreRef}>
              <button
                type="button"
                className={`icon-btn icon-btn--kebab ${openMore ? "active" : ""}`}
                aria-label="More options"
                aria-haspopup="menu"
                aria-expanded={openMore}
                onClick={() => setOpenMore((v) => !v)}
                title="More"
              >
                <IconKebab style={{ color: "currentColor" }} />
              </button>

              {openMore && (
                <div className="more-menu more-menu--nice" role="menu">
                  <div className="more-head">Admin Tools</div>

                  {adminMoreLinks.map((l) => {
                    const ItemIcon = l.icon;
                    return (
                      <NavLink
                        key={l.to}
                        to={l.to}
                        className={({ isActive }) =>
                          `more-item more-item--nice ${isActive ? "active" : ""}`
                        }
                        role="menuitem"
                      >
                        <span className="more-ico">
                          <ItemIcon />
                        </span>
                        <span>{l.label}</span>
                      </NavLink>
                    );
                  })}
                </div>
              )}
            </div>
          )}

          {user ? (
            <>
              <span className="pill">{user.name} ({user.role})</span>
              <button className="btn btn-danger" onClick={logout}>
                Logout
              </button>
            </>
          ) : (
            <>
              <Link className="btn" to="/login">Login</Link>
              <Link className="btn btn-primary" to="/register">Register</Link>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Navbar;
