import React, { useEffect, useMemo, useState } from "react";
import api from "../../api";

const demoCrops = [
  {
    _id: "demo1",
    name: "Rice",
    variety: "BRRI Dhan-28",
    location: "Bogura",
    pricePerKg: 58,
    quantityKg: 1200,
    status: "AVAILABLE",
    image:
      "https://images.unsplash.com/photo-1586201375754-1421e19d5a78?auto=format&fit=crop&w=1200&q=70",
  },
  {
    _id: "demo2",
    name: "Wheat",
    variety: "Shatabdi",
    location: "Rajshahi",
    pricePerKg: 52,
    quantityKg: 800,
    status: "AVAILABLE",
    image:
      "https://images.unsplash.com/photo-1501430654243-c934cec2e1c0?auto=format&fit=crop&w=1200&q=70",
  },
  {
    _id: "demo3",
    name: "Potato",
    variety: "Granola",
    location: "Munshiganj",
    pricePerKg: 32,
    quantityKg: 600,
    status: "AVAILABLE",
    image:
      "https://images.unsplash.com/photo-1567306226416-28f0efdc88ce?auto=format&fit=crop&w=1200&q=70",
  },
  {
    _id: "demo4",
    name: "Tomato",
    variety: "Hybrid",
    location: "Jashore",
    pricePerKg: 70,
    quantityKg: 0,
    status: "SOLD_OUT",
    image:
      "https://images.unsplash.com/photo-1546470427-227e421a3a2f?auto=format&fit=crop&w=1200&q=70",
  },
  {
    _id: "demo5",
    name: "Corn",
    variety: "Sweet Corn",
    location: "Dinajpur",
    pricePerKg: 45,
    quantityKg: 500,
    status: "AVAILABLE",
    image:
      "https://images.unsplash.com/photo-1598032895397-b9472444bf93?auto=format&fit=crop&w=1200&q=70",
  },
  {
    _id: "demo6",
    name: "Onion",
    variety: "Local",
    location: "Pabna",
    pricePerKg: 95,
    quantityKg: 350,
    status: "AVAILABLE",
    image:
      "https://images.unsplash.com/photo-1618512496248-a07fe83aa8cb?auto=format&fit=crop&w=1200&q=70",
  },
];

const CropList = () => {
  const [crops, setCrops] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");

  const [q, setQ] = useState("");
  const [filter, setFilter] = useState("ALL"); // ALL | AVAILABLE | SOLD_OUT

  const fetchCrops = async () => {
    try {
      setErr("");
      setLoading(true);

      const res = await api.get("/crops");
      const list = Array.isArray(res.data) ? res.data : res.data?.crops || [];

      setCrops(list.length ? list : demoCrops);
      if (!list.length) setErr("No crops in database. Showing demo crops.");
    } catch (e) {
      console.error(e);
      setCrops(demoCrops);
      setErr("Could not load crops from server. Showing demo crops.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCrops();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const filtered = useMemo(() => {
    const text = q.trim().toLowerCase();

    return crops.filter((c) => {
      const matchesText =
        !text ||
        (c.name || "").toLowerCase().includes(text) ||
        (c.variety || "").toLowerCase().includes(text) ||
        (c.location || "").toLowerCase().includes(text);

      const status = (c.status || "AVAILABLE").toUpperCase();
      const matchesFilter = filter === "ALL" || status === filter;

      return matchesText && matchesFilter;
    });
  }, [crops, q, filter]);

  return (
    <div className="container">
      <div className="hero">
        <div className="hero-row">
          <div>
            <h1 className="h1">Available Crops</h1>
            <p className="subhead">Browse crops with price and quantity.</p>
          </div>

          <div className="hero-actions">
            <button className="btn" onClick={fetchCrops}>Refresh</button>
          </div>
        </div>
      </div>

      <div className="toolbar">
        <div className="search">
          <input
            className="input"
            placeholder="Search by crop, variety, or location..."
            value={q}
            onChange={(e) => setQ(e.target.value)}
          />
        </div>

        <div className="chips">
          <button className={`chip ${filter === "ALL" ? "active" : ""}`} onClick={() => setFilter("ALL")}>
            All
          </button>
          <button className={`chip ${filter === "AVAILABLE" ? "active" : ""}`} onClick={() => setFilter("AVAILABLE")}>
            Available
          </button>
          <button className={`chip ${filter === "SOLD_OUT" ? "active" : ""}`} onClick={() => setFilter("SOLD_OUT")}>
            Sold Out
          </button>
        </div>
      </div>

      {err && <div className="error">{err}</div>}

      {loading ? (
        <div className="card">Loading crops...</div>
      ) : filtered.length === 0 ? (
        <div className="card">
          <h3 style={{ marginTop: 0 }}>No crops found</h3>
          <p style={{ color: "var(--muted)", marginBottom: 0 }}>
            Try a different search or filter.
          </p>
        </div>
      ) : (
        <div className="crop-grid">
          {filtered.map((c) => {
            const status = (c.status || "AVAILABLE").toUpperCase();
            const soldOut = status === "SOLD_OUT" || (c.quantityKg ?? 0) <= 0;
            const img =
              c.image ||
              c.imageUrl ||
              c.photo ||
              "https://images.unsplash.com/photo-1523413651479-597eb2da0ad6?auto=format&fit=crop&w=1200&q=70";

            return (
              <div className="crop-card" key={c._id}>
                <div className="crop-media">
                  <img
                    className="crop-img"
                    src={img}
                    alt={c.name}
                    onError={(e) => {
                      e.currentTarget.src =
                        "https://images.unsplash.com/photo-1523413651479-597eb2da0ad6?auto=format&fit=crop&w=1200&q=70";
                    }}
                  />
                </div>

                <div className="crop-body">
                  <div className="crop-top">
                    <div>
                      <h3 className="crop-name">{c.name}</h3>
                      <p className="crop-meta">
                        {c.variety ? `${c.variety} • ` : ""}
                        {c.location || "Bangladesh"}
                      </p>
                    </div>

                    <span className={`badge ${soldOut ? "soldout" : ""}`}>
                      {soldOut ? "SOLD OUT" : "AVAILABLE"}
                    </span>
                  </div>

                  <div className="crop-row">
                    <div className="kpi">
                      <p className="label">Price</p>
                      <p className="value">৳ {c.pricePerKg}/kg</p>
                    </div>
                    <div className="kpi">
                      <p className="label">Qty</p>
                      <p className="value">{c.quantityKg} kg</p>
                    </div>
                  </div>

                  <div className="crop-actions">
                    <button className="btn btn-primary" disabled={soldOut}>
                      Buy / Order
                    </button>
                    <button className="btn">Details</button>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default CropList;
